mapper.select:
select * from emp_contract where is_deleted='n' and work_no in
#{item}
and status='A'

select t.* from emp_contract t left join emp_contract_list l on t.work_no=l.work_no and t.contract_code=l.ori_contract_code where t.is_Deleted='n' and t.status ='A' and l.ori_contract_code is null
and t.work_no in
#{item}
and t.end_date <= #{ltEndDate}
order by t.id desc

